function map() {
    var map1 = $('#map');
    if (map1.length) {
        var latlng = new google.maps.LatLng("49.9980554", "36.240871");


        var popupContent = '<div class="catalog-item map-item"><div class="catalog-img"><img src="images/favorite-1.jpg" alt=""></div><div class="catalog-description"><div class="catalog-heading">44 Favorite Place</div><p><strong>.</strong>  : 112 </p><p><strong>:</strong> . , 44  <a href="#">  455 </a></p><div class="catalog-rating"><ul><li><i class="fa fa-star" aria-hidden="true"></i></li><li><i class="fa fa-star" aria-hidden="true"></i></li><li><i class="fa fa-star" aria-hidden="true"></i></li><li><i class="fa fa-star-o" aria-hidden="true"></i></li><li><i class="fa fa-star-o" aria-hidden="true"></i></li></ul><div class="rating-count"><a href=""><span>30</span> </a></div></div><div class="catalog-bottom"><div class="catalog-bottom__left"><strong class="catalog-number">21</strong>  </div><div class="catalog-bottom__btn"><button class="btn btn-success btn-sm"></button></div></div></div></div>';

        var myOptions = {
            zoom: 17,
            center: latlng,
            navigationControlOptions: {
                style: google.maps.NavigationControlStyle.SMALL
            },
            mapTypeId: google.maps.MapTypeId.ROADMAP
        };

        var map = new google.maps.Map(document.getElementById("map"), myOptions);

        map.scrollwheel = false;

        map.setOptions({ mapTypeControl: true });

        var image = 'images/marker.png';
        var marker = new google.maps.Marker({
            position: latlng,
            map: map,
            icon: image
        });


        var infowindow = new google.maps.InfoWindow({
            content: popupContent
        });
        /*infowindow.open(map, marker);*/
        marker.addListener('click', function() {
            infowindow.open(map, marker);
        });

        google.maps.event.addListener(infowindow, 'domready', function() {
            // Reference to the DIV which receives the contents of the infowindow using jQuery
            var iwOuter = $('.gm-style-iw');
            $(iwOuter).next().remove();

            /* The DIV we want to change is above the .gm-style-iw DIV.
             * So, we use jQuery and create a iwBackground variable,
             * and took advantage of the existing reference to .gm-style-iw for the previous DIV with .prev().
             */
            var iwBackground = iwOuter.prev();

            iwOuter.parent().parent().css({ left: '300px', top: '300px' });

            // Remove the background shadow DIV
            iwBackground.children(':nth-child(2)').css({ 'display': 'none' });

            // Remove the white background DIV
            iwBackground.children(':nth-child(4)').css({ 'display': 'none' });

            // Moves the shadow of the arrow 76px to the left margin 
            iwBackground.children(':nth-child(1)').attr('style', function(i, s) { return s + 'left: 0px !important;' });

            // Moves the arrow 76px to the left margin 
            iwBackground.children(':nth-child(3)').find('div').children().remove();

            // Changes the desired color for the tail outline.
            // The outline of the tail is composed of two descendants of div which contains the tail.
            // The .find('div').children() method refers to all the div which are direct descendants of the previous div. 
            iwBackground.children(':nth-child(3)').find('div').children().remove();


        });
    }
}


